func TestHeaderExtraDataCheck(t *testing.T) {
	c := Consortium{
		chainConfig: &params.ChainConfig{
			TrippBlock: common.Big0,
		},
		config: &params.ConsortiumConfig{
			EpochV2: 200,
		},
		isTest:             true,
		testTrippEffective: true,
	}

	// Case 1: not an epoch block, every validator field must be empty
	// non-empty checkpoint validators
	header := types.Header{Number: big.NewInt(100)}
	extraData := finality.HeaderExtraData{
		CheckpointValidators: []finality.ValidatorWithBlsPub{
			{},
		},
	}
	err := c.verifyValidatorFieldsInExtraData(nil, &extraData, &header, nil)
	if !errors.Is(err, consortiumCommon.ErrNonEpochExtraData) {
		t.Fatalf("Expect err: %v got: %v", consortiumCommon.ErrNonEpochExtraData, err)
	}

	// non-empty block producers
	extraData = finality.HeaderExtraData{
		BlockProducers: []common.Address{
			{},
		},
	}
	err = c.verifyValidatorFieldsInExtraData(nil, &extraData, &header, nil)
	if !errors.Is(err, consortiumCommon.ErrNonEpochExtraData) {
		t.Fatalf("Expect err: %v got: %v", consortiumCommon.ErrNonEpochExtraData, err)
	}

	// non-empty block producer bitset
	extraData = finality.HeaderExtraData{
		BlockProducersBitSet: 10,
	}
	err = c.verifyValidatorFieldsInExtraData(nil, &extraData, &header, nil)
	if !errors.Is(err, consortiumCommon.ErrNonEpochExtraData) {
		t.Fatalf("Expect err: %v got: %v", consortiumCommon.ErrNonEpochExtraData, err)
	}

	// Case 2: Not a period block, checkpoint validators must be empty
	header = types.Header{Number: big.NewInt(200)}
	extraData = finality.HeaderExtraData{
		CheckpointValidators: []finality.ValidatorWithBlsPub{
			{},
		},
		BlockProducers: []common.Address{
			{},
		},
	}
	err = c.verifyValidatorFieldsInExtraData(nil, &extraData, &header, nil)
	if !errors.Is(err, consortiumCommon.ErrNonPeriodBlockExtraData) {
		t.Fatalf("Expect err: %v got: %v", consortiumCommon.ErrNonPeriodBlockExtraData, err)
	}

	// Case 3: Before Tripp effective, block producer, block producer bitset must be empty
	c.testTrippEffective = false
	header = types.Header{Number: big.NewInt(200)}
	extraData = finality.HeaderExtraData{
		CheckpointValidators: []finality.ValidatorWithBlsPub{
			{},
		},
		BlockProducers: []common.Address{
			{},
		},
	}
	err = c.verifyValidatorFieldsInExtraData(nil, &extraData, &header, nil)
	if !errors.Is(err, consortiumCommon.ErrPreTrippEpochProducerExtraData) {
		t.Fatalf("Expect err: %v got: %v", consortiumCommon.ErrPreTrippEpochProducerExtraData, err)
	}

	header = types.Header{Number: big.NewInt(200)}
	extraData = finality.HeaderExtraData{
		CheckpointValidators: []finality.ValidatorWithBlsPub{
			{},
		},
		BlockProducersBitSet: 5,
	}
	err = c.verifyValidatorFieldsInExtraData(nil, &extraData, &header, nil)
	if !errors.Is(err, consortiumCommon.ErrPreTrippEpochProducerExtraData) {
		t.Fatalf("Expect err: %v got: %v", consortiumCommon.ErrPreTrippEpochProducerExtraData, err)
	}

	// Case 4: A valid Aaron epoch block
	c.chainConfig.AaronBlock = common.Big0
	c.testTrippEffective = true
	header = types.Header{Number: big.NewInt(200)}
	extraData = finality.HeaderExtraData{
		BlockProducersBitSet: 5,
	}
	err = c.verifyValidatorFieldsInExtraData(nil, &extraData, &header, nil)
	if err != nil {
		t.Fatalf("Expect no error, got: %v", err)
	}
}

func TestEncodeDecodeValidatorBitSet(t *testing.T) {
	candidates := make([]finality.ValidatorWithBlsPub, 10)
	producers := make([]common.Address, 0)
	for i := 0; i < 10; i++ {
		secret, err := ecdsa.GenerateKey(crypto.S256(), rand.Reader)
		if err != nil {
			t.Fatal(err)
		}
		addr := crypto.PubkeyToAddress(secret.PublicKey)
		candidates[i] = finality.ValidatorWithBlsPub{Address: addr}
		if i%2 == 0 {
			producers = append(producers, addr)
		}
	}
	sort.Sort(finality.CheckpointValidatorAscending(candidates))
	enc := encodeValidatorBitSet(candidates, producers)

	// Test encode bit set
	sort.Sort(validatorsAscending(producers))
	indices := enc.Indices()
	if len(indices) != 5 {
		t.Fatalf("mismatch validator1, %v", indices)
	}
	var i int = 0
	for _, idx := range indices {
		if producers[i] != candidates[idx].Address {
			t.Fatal("mismatch validator")
		}
		i += 1
	}

	// Test decode bit set
	dec := decodeValidatorBitSet(enc, candidates)
	if len(dec) != 5 {
		t.Fatal("mismatch validator")
	}
	for i := 0; i < 5; i++ {
		if producers[i] != dec[i] {
			t.Fatal("mismatch validator")
		}
	}
}

func randFieldElement() [32]byte {
	bytes := make([]byte, 32)
	_, err := rand.Read(bytes)
	if err != nil {
		panic("failed to get random field element")
	}
	var r fr.Element
	r.SetBytes(bytes)

	return gokzg4844.SerializeScalar(r)
}

// randBlob generates a random blob with corresponding commitment and proof
func randBlob() (*kzg4844.Blob, *kzg4844.Commitment, *kzg4844.Proof) {
	var blob kzg4844.Blob
	for i := 0; i < len(blob); i += gokzg4844.SerializedScalarSize {
		fieldElementBytes := randFieldElement()
		copy(blob[i:i+gokzg4844.SerializedScalarSize], fieldElementBytes[:])
	}
	commitment, err := kzg4844.BlobToCommitment(&blob)
	if err != nil {
		panic(err)
	}
	proof, err := kzg4844.ComputeBlobProof(&blob, commitment)
	if err != nil {
		panic(err)
	}
	return &blob, &commitment, &proof
}

// randTxsWithBlobs generates a random tx with random blobs, with corresponding sidecar
func randTxsWithBlobs(numBlobs int) (*types.Transaction, *types.BlobTxSidecar) {
	var blobs []kzg4844.Blob
	var commitments []kzg4844.Commitment
	var blobHashes []common.Hash
	var proofs []kzg4844.Proof
	hasher := sha256.New()
	for i := 0; i < numBlobs; i++ {
		blob, commitment, proof := randBlob()
		blobs = append(blobs, *blob)
		commitments = append(commitments, *commitment)
		blobHashes = append(blobHashes, kzg4844.CalcBlobHashV1(hasher, commitment))
		proofs = append(proofs, *proof)
	}
	blobTx := types.BlobTx{
		BlobHashes: blobHashes,
	}
	return types.NewTx(&blobTx), &types.BlobTxSidecar{
		Blobs:       blobs,
		Commitments: commitments,
		Proofs:      proofs,
	}
}

func TestVerifyBlobHeader(t *testing.T) {
	c := Consortium{
		chainConfig: &params.ChainConfig{
			TrippBlock: common.Big0,
		},
		config: &params.ConsortiumConfig{
			EpochV2: 200,
		},
		isTest: true,
	}
	// Create random txs
	var randTx *types.Transaction
	var randSidecar *types.BlobTxSidecar
	var randFakeSidecar *types.BlobTxSidecar

	var txs []*types.Transaction
	var sidecars []*types.BlobTxSidecar
	var fakeSidecars []*types.BlobTxSidecar

	// Create 2 txs with 2 and 3 blobs, respectively
	randTx, randSidecar = randTxsWithBlobs(2)
	_, randFakeSidecar = randTxsWithBlobs(2)
	txs = append(txs, randTx)
	sidecars = append(sidecars, randSidecar)
	fakeSidecars = append(fakeSidecars, randFakeSidecar)

	randTx, randSidecar = randTxsWithBlobs(3)
	_, randFakeSidecar = randTxsWithBlobs(3)
	txs = append(txs, randTx)
	sidecars = append(sidecars, randSidecar)
	fakeSidecars = append(fakeSidecars, randFakeSidecar)

	// Test 1: Block with expired blobs, valid sidecars
	block := types.NewBlock(&types.Header{
		Time: uint64(time.Now().Unix() - int64(params.BlobKeepPeriod) - 1000),
	}, txs, []*types.Header{}, []*types.Receipt{}, trie.NewStackTrie(nil))

	err := c.VerifyBlobHeader(block, &sidecars)
	if err != nil {
		t.Fatal("Expected blob check to be skipped, err:", err)
	}
	if len(sidecars) != 0 {
		t.Fatal("Expected sidecars to be cleared")
	}

	// Test 2: Block with expired blobs, invalid sidecars
	block = types.NewBlock(&types.Header{
		Time: uint64(time.Now().Unix() - int64(params.BlobKeepPeriod) - 1000),
	}, txs, []*types.Header{}, []*types.Receipt{}, trie.NewStackTrie(nil))

	err = c.VerifyBlobHeader(block, &fakeSidecars)
	if err != nil {
		t.Fatal("Expected blob check to be skipped, err:", err)
	}
	if len(fakeSidecars) != 0 {
		t.Fatal("Expected sidecars to be cleared")
	}

	// Create txs again since sidecars are cleared
	txs = []*types.Transaction{}
	randTx, randSidecar = randTxsWithBlobs(2)
	_, randFakeSidecar = randTxsWithBlobs(2)
	txs = append(txs, randTx)
	sidecars = append(sidecars, randSidecar)
	fakeSidecars = append(fakeSidecars, randFakeSidecar)

	randTx, randSidecar = randTxsWithBlobs(3)
	_, randFakeSidecar = randTxsWithBlobs(3)
	txs = append(txs, randTx)
	sidecars = append(sidecars, randSidecar)
	fakeSidecars = append(fakeSidecars, randFakeSidecar)

	// Test 3: Block with valid blobs, valid sidecars
	block = types.NewBlock(&types.Header{
		Time: uint64(time.Now().Unix()),
	}, txs, []*types.Header{}, []*types.Receipt{}, trie.NewStackTrie(nil))

	err = c.VerifyBlobHeader(block, &sidecars)
	if err != nil {
		t.Fatal("Expected blob check to pass, err:", err)
	}
	if len(sidecars) != 2 {
		t.Fatal("Expected sidecars to be kept")
	}

	// Test 4: Block with valid blobs, invalid sidecars
	block = types.NewBlock(&types.Header{
		Time: uint64(time.Now().Unix()),
	}, txs, []*types.Header{}, []*types.Receipt{}, trie.NewStackTrie(nil))

	err = c.VerifyBlobHeader(block, &fakeSidecars)
	if err == nil {
		t.Fatal("Expected blob check to fail due to invalid sidecars")
	}
	if len(fakeSidecars) != 2 {
		t.Fatal("Expected sidecars to be kept")
	}

	// Test 5: Block with valid blobs, valid commitments, but invalid proofs
	block = types.NewBlock(&types.Header{
		Time: uint64(time.Now().Unix()),
	}, txs, []*types.Header{}, []*types.Receipt{}, trie.NewStackTrie(nil))

	err = c.VerifyBlobHeader(block, &[]*types.BlobTxSidecar{
		{
			Blobs:       sidecars[0].Blobs,
			Commitments: sidecars[0].Commitments,
			Proofs:      fakeSidecars[0].Proofs,
		},
		{
			Blobs:       sidecars[1].Blobs,
			Commitments: sidecars[1].Commitments,
			Proofs:      fakeSidecars[1].Proofs,
		},
	})

	// Test 6: Block with valid blobs, invalid sidecars' length
	block = types.NewBlock(&types.Header{
		Time: uint64(time.Now().Unix()),
	}, txs, []*types.Header{}, []*types.Receipt{}, trie.NewStackTrie(nil))
	missingSidecars := sidecars[:len(sidecars)-1]
	err = c.VerifyBlobHeader(block, &missingSidecars)
	if err == nil {
		t.Fatal("Expected blob check to fail due to invalid commitments' length")
	}
	if len(missingSidecars) != 1 {
		t.Fatal("Expected sidecars to be kept")
	}

	// Test 7: Block with exceeding blobs
	randTx, randSidecar = randTxsWithBlobs(2)
	txs = append(txs, randTx)
	sidecars = append(sidecars, randSidecar)

	block = types.NewBlock(&types.Header{
		Time: uint64(time.Now().Unix()),
	}, txs, []*types.Header{}, []*types.Receipt{}, trie.NewStackTrie(nil))

	err = c.VerifyBlobHeader(block, &sidecars)
	if err == nil {
		t.Fatal("Expected blob check to fail due to exceeding blobs")
	}
	if len(sidecars) != 3 {
		t.Fatal("Expected sidecars to be kept")
	}
}

func newDbConfig(scheme string) *trie.Config {
	if scheme == rawdb.HashScheme {
		return trie.HashDefaults
	}
	return &trie.Config{PathDB: pathdb.Defaults}
}
